const mod =251;

function modInverse(a, m) {
    a = ((a % m) + m) % m;
    for (let x = 1; x < m; x++) {
        if ((a * x) % m === 1) {
            return x;
        }
    }
    throw new Error(`Modular inverse does not exist for ${a} mod ${m}`);
}

