import React, { useState } from 'react';
import EncodeForm from './components/EncodeForm';
import DecodeForm from './components/DecodeForm';
import './App.css';

function App() {
  const [isEncodeMode, setIsEncodeMode] = useState(true);

  const toggleMode = () => {
    setIsEncodeMode(prevMode => !prevMode);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Resim Encode/Decode Aracı</h1>
        <button onClick={toggleMode} className="mode-toggle-button">
          {isEncodeMode ? 'Decode Moduna Geç' : 'Encode Moduna Geç'}
        </button>
      </header>
      <main className="App-main">
        {isEncodeMode ? <EncodeForm /> : <DecodeForm />}
      </main>
    </div>
  );
}

export default App;
